mapper.select:
select p.*,
s.name as shopName,
(select user_id from likes l where p.id=l.product_id and l.user_id=#{userId}) as user_id, (select count(*) from likes l where p.id=l.product_id) count from product p
join shoppingmall s on(p.shoppingmall_id=s.id) where shoppingmall_id=#{shoppingmallId}
ORDER BY count desc LIMIT #{offset},#{perPager}
